export declare const typeDefs = "#graphql\n  # ==================== TYPES ====================\n\n  type Usuario {\n    id: ID!\n    email: String!\n    nombre: String!\n    apellido: String!\n    nombreCompleto: String!\n    telefono: String\n    direccion: String\n    rol: Rol!\n    status: UserStatus!\n    fleetType: FleetType\n    zoneId: String\n    lastLogin: String\n    createdAt: String!\n    updatedAt: String!\n  }\n\n  type Rol {\n    id: ID!\n    name: RoleName!\n    description: String\n  }\n\n  type Pedido {\n    id: ID!\n    clienteId: ID!\n    cliente: Usuario\n    repartidorId: ID\n    repartidor: Usuario\n    direccionRecogida: String!\n    direccionEntrega: String!\n    tipoVehiculo: TipoVehiculo!\n    estado: EstadoPedido!\n    costoEstimado: Float!\n    descripcionProducto: String!\n    pesoKg: Float!\n    esFragil: Boolean!\n    fechaCreacion: String!\n    fechaActualizacion: String!\n    tiempoTranscurrido: Int\n    retrasoMin: Int\n    factura: Factura\n  }\n\n  type Vehiculo {\n    id: ID!\n    tipoVehiculo: TipoVehiculo!\n    cilindraje: Int!\n    placa: String!\n    marca: String!\n    color: String!\n    modelo: String!\n    anioFabricacion: String!\n    activo: Boolean!\n    fechaCreacion: String!\n    estado: EstadoVehiculo!\n  }\n\n  type Repartidor {\n    id: ID!\n    identificacion: String!\n    nombre: String!\n    apellido: String!\n    telefono: String!\n    licencia: String!\n    vehiculo: Vehiculo\n    estado: TipoEstado!\n    pedidosActivos: [Pedido!]\n    pedidosCompletados: Int\n  }\n\n  type Factura {\n    id: ID!\n    pedidoId: ID!\n    pedido: Pedido\n    clienteId: ID!\n    cliente: Usuario\n    subtotal: Float!\n    impuestos: Float\n    total: Float!\n    fechaEmision: String!\n    estado: EstadoFactura!\n  }\n\n  type Zona {\n    id: ID!\n    nombre: String!\n    codigo: String!\n    ciudad: String!\n    provincia: String!\n    activa: Boolean!\n    repartidoresDisponibles: Int\n    pedidosPendientes: Int\n  }\n\n  # ==================== AGGREGATION & KPI TYPES ====================\n\n  type FlotaResumen {\n    total: Int!\n    disponibles: Int!\n    enRuta: Int!\n    mantenimiento: Int!\n    inactivos: Int!\n    porTipo: [FlotaPorTipo!]!\n  }\n\n  type FlotaPorTipo {\n    tipo: TipoVehiculo!\n    cantidad: Int!\n    disponibles: Int!\n  }\n\n  type KPIDiario {\n    fecha: String!\n    zonaId: ID\n    totalPedidos: Int!\n    pedidosEntregados: Int!\n    pedidosCancelados: Int!\n    pedidosPendientes: Int!\n    tasaExito: Float!\n    tiempoPromedioEntrega: Float!\n    costoPromedio: Float!\n    ingresoTotal: Float!\n  }\n\n  type EstadisticasPedidos {\n    totalPedidos: Int!\n    porEstado: [PedidoPorEstado!]!\n    porTipoVehiculo: [PedidoPorTipoVehiculo!]!\n    porZona: [PedidoPorZona!]!\n  }\n\n  type PedidoPorEstado {\n    estado: EstadoPedido!\n    cantidad: Int!\n    porcentaje: Float!\n  }\n\n  type PedidoPorTipoVehiculo {\n    tipo: TipoVehiculo!\n    cantidad: Int!\n  }\n\n  type PedidoPorZona {\n    zonaId: String!\n    zonaNombre: String\n    cantidad: Int!\n  }\n\n  type RepartidorDesempeno {\n    repartidor: Usuario!\n    pedidosCompletados: Int!\n    pedidosCancelados: Int!\n    tasaExito: Float!\n    tiempoPromedioEntrega: Float!\n    calificacionPromedio: Float\n  }\n\n  # ==================== ENUMS ====================\n\n  enum RoleName {\n    CLIENTE\n    REPARTIDOR\n    SUPERVISOR\n    GERENTE\n    ADMIN\n  }\n\n  enum UserStatus {\n    ACTIVE\n    INACTIVE\n    SUSPENDED\n  }\n\n  enum FleetType {\n    MOTO\n    CARRO\n    CAMION\n  }\n\n  enum TipoVehiculo {\n    MOTO\n    LIVIANO\n    CAMION\n  }\n\n  enum EstadoPedido {\n    PENDIENTE\n    EN_PROGRESO\n    ENTREGADO\n    CANCELADO\n  }\n\n  enum EstadoVehiculo {\n    DISPONIBLE\n    EN_USO\n    MANTENIMIENTO\n    INACTIVO\n  }\n\n  enum TipoEstado {\n    DISPONIBLE\n    OCUPADO\n    INACTIVO\n  }\n\n  enum EstadoFactura {\n    BORRADOR\n    EMITIDA\n    PAGADA\n    ANULADA\n  }\n\n  # ==================== INPUT TYPES ====================\n\n  input PedidoFiltro {\n    zonaId: ID\n    estado: EstadoPedido\n    tipoVehiculo: TipoVehiculo\n    clienteId: ID\n    repartidorId: ID\n    fechaDesde: String\n    fechaHasta: String\n  }\n\n  input RepartidorFiltro {\n    zonaId: ID\n    estado: TipoEstado\n    fleetType: FleetType\n    disponible: Boolean\n  }\n\n  input KPIFiltro {\n    fecha: String\n    fechaDesde: String\n    fechaHasta: String\n    zonaId: ID\n  }\n\n  # ==================== QUERIES ====================\n\n  type Query {\n    # Pedidos\n    pedido(id: ID!): Pedido\n    pedidos(filtro: PedidoFiltro): [Pedido!]!\n    pedidosPorCliente(clienteId: ID!): [Pedido!]!\n    pedidosPorRepartidor(repartidorId: ID!): [Pedido!]!\n    \n    # Usuarios\n    usuario(id: ID!): Usuario\n    usuarios(rol: RoleName, zoneId: ID, status: UserStatus): [Usuario!]!\n    \n    # Repartidores\n    repartidor(id: ID!): Repartidor\n    repartidores(filtro: RepartidorFiltro): [Repartidor!]!\n    repartidoresDisponibles(zonaId: ID!, fleetType: FleetType!): [Usuario!]!\n    \n    # Flota\n    vehiculo(id: ID, placa: String): Vehiculo\n    vehiculos(estado: EstadoVehiculo, tipoVehiculo: TipoVehiculo): [Vehiculo!]!\n    flotaActiva(zonaId: ID): FlotaResumen!\n    \n    # Facturas\n    factura(id: ID!): Factura\n    facturas(clienteId: ID, estado: EstadoFactura): [Factura!]!\n    \n    # Dashboard & KPIs\n    kpiDiario(filtro: KPIFiltro!): KPIDiario!\n    estadisticasPedidos(filtro: PedidoFiltro): EstadisticasPedidos!\n    desempenoRepartidores(zonaId: ID, fechaDesde: String, fechaHasta: String): [RepartidorDesempeno!]!\n    \n    # Zonas\n    zona(id: ID!): Zona\n    zonas(activa: Boolean): [Zona!]!\n  }\n\n  # ==================== MUTATIONS ====================\n\n  type Mutation {\n    _empty: String\n  }\n";
